export class Legend {
    render() {
        return (h("div", { class: 'as-legend--wrapper' },
            this.renderHeader(),
            this.renderContent()));
    }
    renderContent() {
        if (this.loading) {
            return null;
        }
        return [
            h("div", { class: 'as-legend--legends-slot' },
                h("slot", { name: 'legends' })),
            h("slot", { name: 'footer' })
        ];
    }
    renderHeader() {
        if (this.heading || this.description) {
            return h("as-widget-header", { header: this.heading, subheader: this.description });
        }
    }
    static get is() { return "as-legend"; }
    static get properties() { return {
        "description": {
            "type": String,
            "attr": "description"
        },
        "heading": {
            "type": String,
            "attr": "heading"
        },
        "loading": {
            "type": Boolean,
            "attr": "loading"
        }
    }; }
    static get style() { return "/**style-placeholder:as-legend:**/"; }
}
